using System.Text;

namespace Day_31
{
    class Maximum_Length_Of_Repeated_SubArray
    {
        static int FindLength(int[] A, int[] B)
        {
            int max_length = 0;
            int len_a = A.Length;
            int len_b = B.Length;

            int[][] dp_matrix = new int[len_a + 1][];
            for (int i = 0; i <= len_a; i++)
            {
                dp_matrix[i] = new int[len_b + 1];
            }

            for (int i = 0; i <= len_a; i++)
            {
                for (int j = 0; j <= len_b; j++)
                {
                    dp_matrix[i][j] = 0;
                }
            }

            for (int i = len_a - 1; i >= 0; i--)
            {
                for (int j = len_b - 1; j >= 0; j--)
                {
                    if (A[i] == B[j])
                    {
                        dp_matrix[i][j] = dp_matrix[i + 1][j + 1] + 1;
                        if (max_length < dp_matrix[i][j])
                        {
                            max_length = dp_matrix[i][j];
                        }
                    }
                }
            }
            return max_length;
        }
