    class Maximum_Length_Of_Repeated_SubArray
    {
        static int FindLength(int[] A, int[] B)
        {
            int max = 0;
            for (int i = 0; i < A.Length; i++)
            {
                max = FindLength(A, B, i, max);
            }
            return max;
        }
        static int FindLength(int[] A, int[] B, int startingPoint, int length)
        {
            List<int> possible_entrances = new List<int>();
            for (int i = 0; i < B.Length; i++)
            {
                if (B[i] == A[startingPoint])
                {
                    possible_entrances.Add(i);
                }
            }
            foreach (int i in possible_entrances)
            {
                int counter = 0;
                if (i + length < B.Length && startingPoint + length < A.Length && B[i + length] != A[startingPoint + length])
                {
                    continue;
                }
                while (i + counter < B.Length && startingPoint + counter < A.Length && B[i + counter] == A[startingPoint + counter])
                {
                    counter++;
                }
                if (counter > length)
                {
                    length = counter;
                }
            }
            return length;
        }

